// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Royalty.sol";
import "base64-sol/base64.sol";
import "./LicenseActivation.sol";

error ERC721Metadata__URI_QueryFor_NonExistentToken();
error PerpetualLicense__TransferFailed();
error PerpetualLicense__NeedMoreETHSent();
error PerpetualLicense__LicenseActivated();

contract PerpetualLicense is ERC721Royalty, Ownable {
    uint256 private s_tokenCounter;
    uint256 private s_licensePrice;
    string private i_companyName;
    string private i_licenseType;
    string private i_licenseName;
    string private i_licenseAgreementUrl;

    LicenseActivation private s_licenseActivationContract;

    event CreatedLicenseToken(uint256 indexed tokenId, uint256 licensePrice);

    constructor(
        string memory companyName,
        string memory licenseName,
        string memory licenseAgreementUrl,
        uint256 licensePrice,
        uint96 royaltyPercentage,
        address licenseActivationContractAddress
    ) ERC721("Software License", "SHK") {
        s_tokenCounter = 0;
        s_licensePrice = licensePrice;
        i_companyName = companyName;
        i_licenseName = licenseName;
        i_licenseAgreementUrl = licenseAgreementUrl;
        i_licenseType = "Perpetual";
        _setDefaultRoyalty(msg.sender, royaltyPercentage * 100);

        s_licenseActivationContract = LicenseActivation(licenseActivationContractAddress);
    }


    function buyToken() public payable  {
        if (msg.value < s_licensePrice) {
            revert PerpetualLicense__NeedMoreETHSent();
        }
        _safeMint(msg.sender, s_tokenCounter);
        s_tokenCounter = s_tokenCounter + 1;
        emit CreatedLicenseToken(s_tokenCounter, s_licensePrice);
    }

    function mintToken(address customer) public onlyOwner  {

        _safeMint(customer, s_tokenCounter);
        s_tokenCounter = s_tokenCounter + 1;
        emit CreatedLicenseToken(s_tokenCounter, s_licensePrice);
    }

    function _baseURI() internal pure override returns (string memory) {
        return "data:application/json;base64,";
    }

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        if (!_exists(tokenId)) {
            revert ERC721Metadata__URI_QueryFor_NonExistentToken();
        }

        return
            string(
                abi.encodePacked(
                    _baseURI(),
                    Base64.encode(
                        bytes(
                            abi.encodePacked(
                                '{"name":"',
                                i_companyName, 
                                '","license name":"',
                                i_licenseName,
                                '","license agreement Url":"',
                                i_licenseAgreementUrl,
                                '","license Type":"',
                                i_licenseType,
                                '","price":"',
                                Strings.toString(s_licensePrice),
                                '","tokenID":"',
                                Strings.toString(tokenId),
                                '"}'
                            )
                        )
                    )
                )
            );
    }


    function getTokenCounter() public view returns (uint256) {
        return s_tokenCounter;
    }

    function getLicensePrice() public view returns (uint256) {
        return s_licensePrice ;
    }

    function withdraw() public onlyOwner {
        uint256 amount = address(this).balance;
        (bool success, ) = payable(msg.sender).call{value: amount}("");
        if (!success) {
            revert PerpetualLicense__TransferFailed();
        }
    }

    function updateLicensePrice(uint256 newPrice) public onlyOwner {
        require(newPrice > 0, "Price must be greater than zero");
        s_licensePrice = newPrice;
    }

    function _transfer(address from, address to, uint256 tokenId) internal virtual override {
        require(
            !s_licenseActivationContract.isLicenseActivated(tokenId),
            "Cannot transfer an activated license"
        );

        super._transfer(from, to, tokenId);
    }
}

contracts/SubscriptionLicense.sol.content:
//SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract SubscriptionLicense {
    using ECDSA for bytes32;
    using SafeMath for uint256;


    address private author;

    address private requiredToAddress;
    address private requiredTokenAddress;
    uint256 private requiredTokenAmount;
    uint256 private requiredPeriodSeconds;
    uint256 private requiredGasPrice;
    string private i_licenseName;

    mapping(bytes32 => uint256) private nextValidTimestamp;

    event ExecuteSubscription(
        address indexed from, 
        address indexed to, 
        address tokenAddress, 
        uint256 tokenAmount, 
        uint256 periodSeconds, 
        uint256 gasPrice, 
        uint256 nonce 
    );

    event CancelSubscription(
        address indexed from, 
        address indexed to, 
        address tokenAddress, 
        uint256 tokenAmount, 
        uint256 periodSeconds, 
        uint256 gasPrice, 
        uint256 nonce 
    );

    constructor(
        address _toAddress,
        address _tokenAddress,
        uint256 _tokenAmount,
        uint256 _periodSeconds,
        uint256 _gasPrice,
        string memory licenseName
    ) {
        requiredToAddress=_toAddress;
        requiredTokenAddress=_tokenAddress;
        requiredTokenAmount=_tokenAmount;
        requiredPeriodSeconds=_periodSeconds;
        requiredGasPrice=_gasPrice;
        author=msg.sender;
        i_licenseName = licenseName;
    }


    function isSubscriptionActive(
        bytes32 subscriptionHash,
        uint256 gracePeriodSeconds
    )
        external
        view
        returns (bool)
    {

        if(nextValidTimestamp[subscriptionHash]==type(uint).max){
          return false;
        }
        return (block.timestamp.add(gracePeriodSeconds) <=
                nextValidTimestamp[subscriptionHash]
        );
    }

    function getSubscriptionHash(
        address from, 
        address to, 
        address tokenAddress, 
        uint256 tokenAmount, 
        uint256 periodSeconds, 
        uint256 gasPrice, 
        uint256 nonce 
    )
        public
        view
        returns (bytes32)
    {

        require( to == requiredToAddress, "requiredToAddress Failure" );
        require( tokenAddress == requiredTokenAddress, "requiredTokenAddress Failure"  );
        require( tokenAmount == requiredTokenAmount, "requiredTokenAmount Failure"  );
        require( periodSeconds == requiredPeriodSeconds, "requiredPeriodSeconds Failure"  );
        require(gasPrice == requiredGasPrice, "requiredGasPrice Failure"  );

        return keccak256(
            abi.encodePacked(
                bytes1(0x19),
                bytes1(0),
                address(this),
                from,
                to,
                tokenAddress,
                tokenAmount,
                periodSeconds,
                gasPrice,
                nonce
        ));
    }


    function getSubscriptionSigner(
        bytes32 subscriptionHash, 
        bytes memory  signature 
    )
        public
        pure
        returns (address)
    {
        return subscriptionHash.toEthSignedMessageHash().recover(signature);
    }

    function isSubscriptionReady(
        address from, 
        address to, 
        address tokenAddress, 
        uint256 tokenAmount, 
        uint256 periodSeconds, 
        uint256 gasPrice, 
        uint256 nonce,
        bytes memory  signature 
    )
        external
        view
        returns (bool)
    {
        bytes32 subscriptionHash = getSubscriptionHash(
            from, to, tokenAddress, tokenAmount, periodSeconds, gasPrice, nonce
        );
        address signer = getSubscriptionSigner(subscriptionHash, signature);
        uint256 allowance = ERC20(tokenAddress).allowance(from, address(this));
        uint256 balance = ERC20(tokenAddress).balanceOf(from);

        return (
            signer == from &&
            from != to &&
            block.timestamp >= nextValidTimestamp[subscriptionHash] && // nextValidTimestamp[subscriptionHash] default value is zero
            allowance >= tokenAmount.add(gasPrice) &&
            balance >= tokenAmount.add(gasPrice)
        );
    }


    function cancelSubscription(
        address from, 
        address to, 
        address tokenAddress, 
        uint256 tokenAmount, 
        uint256 periodSeconds, 
        uint256 gasPrice, 
        uint256 nonce, 
        bytes memory  signature 
    )
        external
        returns (bool success)
    {
        bytes32 subscriptionHash = getSubscriptionHash(
            from, to, tokenAddress, tokenAmount, periodSeconds, gasPrice, nonce
        );
        address signer = getSubscriptionSigner(subscriptionHash, signature);


        require(signer == from, "Invalid Signature for subscription cancellation");


        require(from == msg.sender, 'msg.sender is not the subscriber');


        nextValidTimestamp[subscriptionHash]=type(uint).max;

        emit CancelSubscription(
            from, to, tokenAddress, tokenAmount, periodSeconds, gasPrice, nonce
        );

        return true;
    }


    function executeSubscription(
        address from, 
        address to, 
        address tokenAddress, 
        uint256 tokenAmount, 
        uint256 periodSeconds, 
        uint256 gasPrice, 
        uint256 nonce, 
        bytes memory signature 
    )
        public
        returns (bool success)
    {

        require(this.isSubscriptionReady(from, to, tokenAddress, tokenAmount, periodSeconds, gasPrice, nonce, signature), "Subscription is not ready or conditions of transction are not met");
        bytes32 subscriptionHash = getSubscriptionHash(
            from, to, tokenAddress, tokenAmount, periodSeconds, gasPrice, nonce
        );

        nextValidTimestamp[subscriptionHash] = block.timestamp.add(periodSeconds);


        uint256 startingBalance = ERC20(tokenAddress).balanceOf(to);
        ERC20(tokenAddress).transferFrom(from,to,tokenAmount);
        require(
          (startingBalance+tokenAmount) == ERC20(tokenAddress).balanceOf(to),
          "ERC20 Balance did not change correctly"
        );

        require(
          checkSuccess(),
          "Subscription::executeSubscription TransferFrom failed"
          );


        emit ExecuteSubscription(
            from, to, tokenAddress, tokenAmount, periodSeconds, gasPrice, nonce
        );


        if (gasPrice > 0) {
            ERC20(tokenAddress).transferFrom(from, msg.sender, gasPrice);
            require(
                checkSuccess(),
                "Subscription::executeSubscription Failed to pay gas as from account"
            );
        }

        return true;
    }


    function checkSuccess(
    )
        private
        pure
        returns (bool)
    {
        uint256 returnValue = 0;
        assembly {
            switch returndatasize()
            case 0x0 {
                returnValue := 1
            }
            case 0x20 {
                returndatacopy(0x0, 0x0, 0x20)
                returnValue := mload(0x0)
            }
            default { }
        }

        return returnValue != 0;
    }

    function getRequiredToAddress() external view returns (address) {
        return requiredToAddress;
    }

    function getRequiredTokenAddress() external view returns (address) {
        return requiredTokenAddress;
    }

    function getRequiredTokenAmount() external view returns (uint256) {
        return requiredTokenAmount;
    }

    function getRequiredPeriodSeconds() external view returns (uint256) {
        return requiredPeriodSeconds;
    }

    function getRequiredGasPrice() external view returns (uint256) {
        return requiredGasPrice;
    }

    function getILicenseName() external view returns (string memory) {
        return i_licenseName;
    }
}
